Promise.all(["config","products","categories","farms","payments","socials","promos","banner","typography","productModal","categoriesEnabled","farmsEnabled","loadingscreen","cart_services","cart_slots","maintenance"].map(k=>async function(name){try{const res=await fetch(`/api/db/${name}.json?t=${Date.now()}`,{cache:"no-store"});if(!res.ok)return;const data=await res.json();localStorage.setItem(`site_${name}`,JSON.stringify(data)),localStorage.setItem(`site_${name}_timestamp`,Date.now().toString()),window.dispatchEvent(new CustomEvent("adminDataUpdated",{detail:{key:name,data:data}}))}catch(err){}}(k)));